document.addEventListener("DOMContentLoaded", function(event) {
  
  window.addEventListener("hashchange", function(e){

    var next = document.getElementsByClassName('installer');
    var nextid = e.newURL[e.newURL.length - 1];
    var previd = (e.newURL[e.newURL.length - 1] - 1);
    var prevelement = document.getElementById("step"+previd);
    var nextelement = document.getElementById("step"+nextid);

    prevelement.style.left = "-100%";
    nextelement.style.left = "50%";

    history.pushState("", document.title, window.location.pathname);
  });
});
